Nice puzzle.  Quite easy if you have a trick in mind to guide copilot.  Here is my solution:

#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
#include <climits>
#include <set>

using namespace std;

std::pair<int, int> find_substring(const std::string& s, const std::string& chars) {
  std::vector<int> last_seen(256, -10000);

  int best_begin = -10000;
  int best_end = 0;

  for (int i = 0; i < s.size(); ++i) {
    int min_index = INT_MAX;
    int max_index = -1;
    char cs = s[i];
    last_seen[static_cast<unsigned char>(cs)] = i;

    // find the minimum and maximum of the last seen indexes of the characters
    for (char c : chars) {
      min_index = std::min(min_index, last_seen[static_cast<unsigned char>(c)]);
      max_index = std::max(max_index, last_seen[static_cast<unsigned char>(c)]);
    }

    // check if the current substring is smaller than the best found so far
    if (max_index - min_index < best_end - best_begin) {
      best_begin = min_index;
      best_end = max_index;
    }
  }

  return std::make_pair(best_begin, best_end);
}

std::string expand_characters(const std::string& input) {
  std::set<char> chars;
  size_t start = 0;
  while (start < input.size()) {
    if (input[start] == '-') { // range
      char begin = input[start - 1];
      char end = input[start + 1];
      for (char c = begin; c <= end; ++c) {
        chars.insert(c);
      }
      start += 2;
    } else if (input[start] == ',') { // separator
      ++start;
    } else { // single character
      chars.insert(input[start]);
      ++start;
    }
  }
  std::string result;
  for (char c : chars) {
    result += c;
  }
  return result;
}

int main() {
  int chars_per_line, num_lines;
  std::cin >> chars_per_line >> num_lines;

  std::string s;
  for (int i = 0; i < num_lines; ++i) {
    std::string line;
    std::cin >> line;
    s += line;
  }

  int tests;
  std::cin >> tests;

  for (int i = 0; i < tests; i++) {
    std::string input;
    std::cin >> input;
    std::string chars = expand_characters(input);
    std::pair<int, int> result = find_substring(s, chars);
    std::cout << result.first << " " << result.second << std::endl;
  }
  return 0;
}
