const orderNumber = readline(); /**Input string */
var orderNumberFixed = ''; /*blank string for later modification, in the event the order# is wrong */

var kb = "QWERTYUIOPASDFGHJKL;ZXCVBNM,./" /*keyboard without any modifications */
var num = '*7894561230'/*numpad w/o any modifications */


/*eval to determine if order# is wrong. If it isn't, it prints the input. */
if(orderNumber.includes("0")) console.log(orderNumber); else { 
    var orderNumberSplit = orderNumber.split('');/*if it is wrong, we're going to split up the input to an array*/
    orderNumberSplit.forEach(character => {/*then, cycle through array and type it in reverse*/
        if(kb.includes(character))orderNumberFixed += kb[kb.length-kb.indexOf(character)-1];
        if(num.includes(character))orderNumberFixed += num[num.length-num.indexOf(character)-1];
    })
    console.log(orderNumberFixed)/*printing final product :)*/
}
