package main

import (
    "fmt"
    "bufio"
    "strings"
    "os"
    "strconv"
)

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/

func main() {
    scanner := bufio.NewScanner(os.Stdin)
    scanner.Buffer(make([]byte, 1000000), 1000000)

    scanner.Scan()
    dday := scanner.Text()

    // Declare a map to store days in a month
    dayByMonth := map[int]int{
        1: 31,  // January
        2: 28,  // February (ignoring leap years for simplicity)
        3: 31,  // March
        4: 30,  // April
        5: 31,  // May
        6: 30,  // June
        7: 31,  // July
        8: 31,  // August
        9: 30,  // September
        10: 31, // October
        11: 30, // November
        12: 31, // December
    }

    // Split the string by "/" to get individual components
    dateComponents := strings.Split(dday, "/")

    // Convert each component to an integer using strconv.Atoi
    year, err := strconv.Atoi(dateComponents[0])
    month, err := strconv.Atoi(dateComponents[1])
    day, err := strconv.Atoi(dateComponents[2])
    _ = err // to avoid unused error

    // Number of day
    var nbDay int
    for i:=0 ; i < month ; i++ {
        nbDay += dayByMonth[i]
    }
    nbDay += day

    // Leap year?
    var nbDayInYear int
    if (year%4 == 0 && year%100 != 0) || (year%400 == 0)  {
        // Yes it's a leap year
        nbDayInYear = 366
        if month >= 3 {
            nbDay++
        }
    } else {
        nbDayInYear = 365
    }

    // Write answer to stdout
    fmt.Printf("%.02f", float64(nbDay)/float64(nbDayInYear)*100)
}
