import java.util.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int n = in.nextInt();

        String[] actions = {
            "Send Message",
            "Acknowledge",
            "Receive Notification",
            "Update Status"
        };


        if (n == 0) {
            System.out.println("null");
        } else {
            for (int i = 0; i < actions.length; i++) {
                if ((n & (1 << i)) != 0) {
                    System.out.println(actions[i]);
                }
            }
        }
    }
}
