fn read_line() -> String {
    std::io::stdin()
        .lines()
        .next()
        .expect("more lines expected")
        .expect("unable to read line correctly")
}

fn main() {
    let first_line: Vec<i32> = read_line()
        .split(" ")
        .map(|x| x.parse::<i32>().unwrap())
        .collect();
    let n = first_line[0];
    let b = first_line[1];

    let mut highest_score = 0;
    let mut best_climber = String::new();

    for _ in 0..n {
        let input = read_line();
        let parts: Vec<&str> = input.trim().split_whitespace().collect();

        let name = parts[0];
        let score: usize = parts[1..]
            .iter()
            .map(|&s| {
                let attempt = s.parse::<usize>().unwrap();
                if attempt == 1 {
                    25 + 10
                } else if attempt >= 2 {
                    1.max(25usize.checked_sub(attempt - 1).unwrap_or(0))
                } else {
                    0
                }
            })
            .sum();

        if score >= highest_score {
            highest_score = score;
            best_climber = name.to_string();
        }
    }

    println!("{best_climber} {highest_score}");
}
