#include <stdlib.h>
#include <stdio.h>
#include <string.h>
#include <stdbool.h>

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/

int main()
{
    int N;
    scanf("%d", &N);

    char store[101];
    for(int i = 0; i < 101; i++)
        store[i] = '*';
    int count = 0;
    for (int i = 0; i < N; i++) {
        char line[101];
        scanf("%s", line);

        for(int j = 0; line[j]; j++)
        {
            if(line[j] != '*')
            {
                if(j > 0)
                {
                    if(line[j - 1] == '*' && store[j] == '*')
                        count++;
                }
                else
                {
                    if(store[j] == '*')
                        count++;
                }
            }
            store[j] = line[j];
        }
    }

    // Write an answer using printf(). DON'T FORGET THE TRAILING \n
    // To debug: fprintf(stderr, "Debug messages...\n");

    printf("%i\n", count);

    return 0;
}
