shortest=false;



if(!shortest){

    const wallTypes={W:1,S:2,M:4,A:8} //All wall types' first letter, paired with their health/2 
    const doorTypes={W:1,S:1,G:2,A:3} //Or, in the event they only have 1 or less hp, i'm making it 1. 
        //The reasoning here is that, since c4 does 2hp, and you need at least 1, this will make the numbers more
        //simiplistic to deal with, rather than converting them somewhere else in the code

    let costOfDoors=0                   //placeholder variables for the total cost off the walls and doors together
    let costOfWalls=0                   //to find out which one's cheaper to raid

    const w = parseInt(readline()) //amount of walls

    for (let i = 0; i < w; i++) { //looping for each wall

        const wall = readline(); //wall = wall type

        console.error(wall+" has "+wallTypes[wall[0]] +"HP"); //error logging for debuging

        costOfWalls += +wallTypes[wall[0]] //adding the wall type's HP to the total cost of walls 
            //by looking in wallTypes for first letter of wall

    }

    //Same code for doors, just for doors instead of walls
    const d = parseInt(readline());
    for (let i = 0; i < d; i++) {
        const door = readline();
        console.error(door+" has "+doorTypes[door[0]] +"HP");
        costOfDoors += +doorTypes[door[0]]
    }

    //error logging for debugging
    console.error(costOfWalls + " is the total cost of the walls")
    console.error(costOfDoors + " is the total cost of the doors")
    //error logging for debugging

    //Determines which is larger, and prints the appropriate text
    if(costOfDoors < costOfWalls) console.log(costOfDoors + " C4 needed to go through " + d + " door(s)")
        else console.log(costOfWalls + " C4 needed to go through " + w + " wall(s)")



}else{
    //Shortest, for fun :)
    r=readline
    a={W:1,S:2,M:4,A:8}
    b={W:1,S:1,G:2,A:3}
    c=0
    e=0
    w=r();for(i=0;i<w;i++)c+=+a[r()[0]]
    d=r();for(i=0;i<d;i++)e+=+b[r()[0]]
    o=e<c?o=e:c
    t=e<c?d+' door(s)':w+' wall(s)'
    console.log(o+" C4 needed to go through "+t)
}

