#include <stdlib.h>
#include <stdio.h>
#include <string.h>
#include <stdbool.h>

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/

int main()
{
    char name[101];
    scanf("%[^\n]", name);
    char format[13];
    scanf("%s", format);

    int type = (strcmp(format, "PascalCase") == 0) ? 1: (strcmp(format, "camelCase") == 0) ? 2 : 3;
    int start = (type == 1) ? 1 : 0;
    int printed = 0;
    for(int i = 0; name[i]; i++)
    {
        if(name[i] == ' ')
        {
            if(printed && start == 0 && type == 3)
                printf("_");
            if(printed)
                start = 1;
        }
        else
        {
            if(start && (type == 1 || type == 2))
                printf("%c", toupper(name[i]));
            else
                printf("%c", tolower(name[i]));
            start = 0;
            printed = 1;
        }
    }
    printf("\n");
    return 0;
}
