const S = readline();
isPalindrome = true
for(let x = 0; x<S.length; x++){
    if (S[x]!=S[S.length-x-1]){
        isPalindrome = false
        break
    }
}
if (isPalindrome){
    console.log('Strong palindrome')
} else{
    trimmed = S.replaceAll(/\W/g,'').toLowerCase()
    isPalindrome = true
    for(let x = 0; x<trimmed.length; x++){
        if (trimmed[x]!=trimmed[trimmed.length-x-1]){
            isPalindrome = false
            break
        }
    }
    console.log(isPalindrome?'Weak palindrome':'Not a palindrome');
}
