program Answer;
{$H+}
{$mode Delphi}

uses
  sysutils, classes, math, types;

procedure ParseIn(Inputs: TStrings) ;
var Line : string;
begin
  readln(Line);
  Inputs.Clear;
  Inputs.Delimiter := ' ';
  Inputs.DelimitedText := Line;
end;

const
  GelPush = 5; // ml

var
  GelAmount : Int32;
  GuestCount : Int32;
  GuestName : String;
  i, j, sum, dsum : Int32;
  mlUsed : extended;
  Inputs: TStringList;

begin
  mlUsed:=0;
  Inputs := TStringList.Create;
  ParseIn(Inputs);
  GelAmount := StrToInt(Inputs[0]);
  ParseIn(Inputs);
  GuestCount := StrToInt(Inputs[0]);
  for i := 0 to GuestCount-1 do
  begin
    readln(GuestName);
    sum:=0;
    dsum:=0;
    for j := 1 to Length(GuestName) do
      sum:=sum+ord(GuestName[j]);
    while sum > 9 do
    begin
      dsum:=0;
      while sum > 0 do
      begin
        dsum:=dsum+(sum mod 10);
        sum:=sum div 10;
      end;
      sum:=dsum;
    end;
    mlUsed:=mlUsed+dsum*GelPush;
  end;

  if mlUsed <= GelAmount then
    WriteLn(Format('%.0f%%', [100*((GelAmount-mlUsed) / 1000)-0.5]))
  else
    WriteLn('not enough');
  flush(StdErr); flush(output); // DO NOT REMOVE
end.
