address = input()

count_IPv4 = 0
count_IPv6 = 0
none = False
valid_letters_in_ipv6 = ["a", "b", "c", "d", "e", "f"]
valid_part = True

if len(address) > 15:
    address = address.split(":")

    if len(address) != 8 and address.count("") == 0:
        none = True
    else:
        for text in address:

            if "-" in text or len(text) > 4 and text != "0":

                none = True
                break

            else:
                for letter in text:
                    if letter.isalpha() and not letter.lower() in  valid_letters_in_ipv6:

                        valid_part = False
                        break

                if valid_part == True:

                    if(text == ""):
                        count_IPv6 += 1
                    elif int(text, 16)  >= 0 and int(text, 16)  <= 65535:

                        count_IPv6 += 1
                    else:
                        valid_part = False

    if count_IPv6 == len(address):
        print("IPv6")
    elif(none == True or valid_part == False):
        print("NONE")
else:
    address = address.split(".")
    if(len(address) != 4):
        print("NONE")
    else:
        for text in address:
            if "-" in text or text == "" or (int(text) < 0 or int(text) > 255):
                none = True
                break
            elif text.isdigit() and int(text) >= 0 and int(text) <= 255:
                count_IPv4 += 1

        if count_IPv4 == 4:
            print("IPv4")
        elif(none == True):
            print("NONE")
