def zongo(n):
  l=[n,0]
  v=1
  cont=True
  while cont:
    cont=False
    for i,e in enumerate(l):
      if e>1:
        cont=True
        if i==0:
          l.insert(0,0)
          v+=1
          i=1
        while i+2>=len(l):
          l.append(0)
        l[i-1]+=1
        l[i]-=2
        l[i+2]+=1
        break
    for i,e in enumerate(l[:-1]):
      if e>0 and l[i+1]>0:
        cont=True
        if i==0:
          l.insert(0,0)
          v+=1
          i=1
        l[i-1]+=1
        l[i]-=1
        l[i+1]-=1
        break
  m=["%dL"%(v-1-i) for i in range(v-1) if l[i]]
  if l[v-1]:
    m.append("C")
  m+=["%dR"%(i+1) for i,e in enumerate(l[v:]) if e]
  return " ".join(m)

print(zongo(int(input())))
