for (var i = 8; i--;) {
    var j = readline().indexOf('N');
    if (~j) {
        var options = '1.1,1,.1,'.split(',').map(v => {
            var [a, b] = v.split('.');
            return [+a || -1, +b || -1];
        });
        var x = j,
            y = i;
        options = [].concat(...options.map(([dx, dy]) => [[dx, dy * 2], [dx * 2, dy]]));
        const inRange = n => 0 <= n && n < 8
        print(options.map(([dx, dy]) => [x + dx, y + dy])
                        .filter(([x, y]) => inRange(x) && inRange(y))
                        .map(([x, y]) => 'abcdefgh'[x] + (y + 1))
                        .sort()
                        .join('\n'));
        break;
    }
}
