#include <stdio.h>
#include <string.h>
#include <ctype.h>

int countVowels(char* str)
{
	int num = 0;
	for(int i = 0, len = strlen(str); i < len; i++)
	{
		switch(toupper(str[i]))
		{
			case 'A':
			case 'E':
			case 'I':
			case 'O':
			case 'U':
				num++;
		}
	}
	return num;
}

int main(void)
{
    char str[1024];
    fgets(str, sizeof(str), stdin);

    char* ptr = strtok(str, " ");
    while(ptr)
    {
    	int num = countVowels(ptr);
    	printf("%d", num);

    	ptr = strtok(NULL, " ");

    	if(ptr)
    		printf(" ");
    }

    return 0;
}
