def counter(x, y):
    global m, width, height

    counted = 0

    if x == 0 and x == width - 1:
        situations_x = [0]
    elif x == 0:
        situations_x = [0, 1]
    elif x == width - 1:
        situations_x = [-1, 0]
    else:
        situations_x = [-1, 0, 1]

    if y == 0 and y == height - 1:
        situations_y = [0]
    elif y == 0:
        situations_y = [0, 1]
    elif y == height - 1:
        situations_y = [-1, 0]
    else:
        situations_y = [-1, 0, 1]

    for xi in situations_x:
        for yi in situations_y:
            if m[y + yi][x + xi] == "#" and not (xi == yi and xi == 0):
                counted += 1
    return counted


width, height = [int(i) for i in input().split()]
old_steps = []
m = []
for i in range(height):
    line = input()
    m.append([i for i in line])

dead_step = [[" "]*width for i in range(height)]

while m != dead_step and m not in old_steps:
    old_steps.append(m.copy())
    alive = []
    dead = []

    for y in range(height):
        for x in range(width):
            if counter(x, y) == 3:
                alive.append([x, y])
            elif counter(x, y) < 2 or counter(x, y) > 3:
                dead.append([x, y])

    n = [[" "]*width for i in range(height)]

    for y in range(height):
        for x in range(width):
            if [x, y] in alive:
                n[y][x] = "#"
            elif [x, y] in dead:
                n[y][x] = " "
            else:
                n[y][x] = m[y][x]

    m = n

if m == dead_step:
    print("Death")
elif old_steps[-1] == m:
    print("Still")
elif m in old_steps:
    period = 0
    for i, x in enumerate(reversed(old_steps)):
        if x == m:
            period = i
    print("Oscillator " + str(period+1))
