program Answer;
{$H+}
{$mode Delphi}

uses
  sysutils, classes, math, types;

const
  MeatDensity = 1200; // [kg/m³]
  BratwurstLength = 0.08; // [m]

var
  i, cnt : Int32;
  GroundMeatMix : Int32;
  s : String;
  GateKeyHoleDiameter : Integer;
  q : Extended;

begin
  ReadLn(s);
  GroundMeatMix := StrToInt(s);
  q:=0.0;
  for i := 0 to 3 do
  begin
    ReadLn(s);
    GateKeyHoleDiameter := StrToInt(s);
    q:=q+sqr(GateKeyHoleDiameter/2000);
  end;
  cnt:=Round(GroundMeatMix / (MeatDensity*Pi*BratwurstLength*q));
  WriteLn(cnt);
  flush(StdErr); flush(output); // DO NOT REMOVE
end.
