import java.util.*;

class Solution {
    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        int L = in.nextInt();
        boolean link[][] = new boolean[N][N];
        boolean checked[] = new boolean[N];
        for (int i = 0; i < L; i++) {
            int N1 = in.nextInt();
            int N2 = in.nextInt();
            link[N1][N2] = link[N2][N1] = true;
        }
        int S = in.nextInt();
        int E = in.nextInt();
        Vector<Integer> points = new Vector<Integer>();
        points.add(S);
        int time = 0;
        int srt = 0;
        outer:
        for (int k = 0; k < N; k++) {
            int lnt = points.size();
            for (int i = srt; i < lnt; i++,srt++) {
                int cnt = points.elementAt(i);
                checked[cnt] = true;
                if (cnt==E) {break outer;}
                for (int j = 0; j < N; j++) {
                    if (link[cnt][j] && !checked[j]) {points.add(j);}
                }
            }
            time++;
        }
        System.out.println(time);
    }
}
