using System;

class Program
{
    static void Main(string[] args)
    {
        int n = int.Parse(Console.ReadLine());

        var twins = FindNextPrimeTwins(n);
        Console.WriteLine($"{twins.Item1} {twins.Item2}");
    }

    // Function to find the next pair of prime twins after n
    static Tuple<int, int> FindNextPrimeTwins(int n)
    {
        int first = n % 2 == 0 ? n + 1 : n + 2;  // Start checking from the next odd number if n is even
        while (true)
        {
            if (IsPrime(first) && IsPrime(first + 2))
            {
                return Tuple.Create(first, first + 2);
            }
            first += 2;  // Increment by 2 to check only odd numbers
        }
    }

    // Optimized function to check if a number is prime
    static bool IsPrime(int x)
    {
        if (x <= 1) return false;
        if (x == 2) return true;  // 2 is the only even number that is prime
        if (x % 2 == 0) return false;  // No other even number is prime
        for (int i = 3; i * i <= x; i += 2)  // Check only odd divisors
        {
            if (x % i == 0) return false;
        }
        return true;
    }
}
