const decimalRomanMap: Map<number, string> = new Map<number, string>([
    [1000, "M"],
    [900, "CM"],
    [500, "D"],
    [400, "CD"],
    [100, "C"],
    [90, "XC"],
    [50, "L"],
    [40, "XL"],
    [10, "X"],
    [9, "IX"],
    [5, "V"],
    [4, "IV"],
    [1, "I"]
]);

const decimalToRoman = (num: number): string => {
    if (num < 1) return "NONE";
    let romanNumber: string = "";
    for (const [value, symbol] of decimalRomanMap) {
        while (num >= value) {
            romanNumber += symbol;
            num -= value;
        }
    }
    return romanNumber;
}

const input: number = parseInt(readline());
console.log(decimalToRoman(input));

