formula = input()
n = int(input())
values = {}
for i in range(n):
    name, value = input().split()
    if value == "TRUE":
        value = "True"
    if value == "FALSE":
        value = "False"
    values[name] = value

formula = formula.replace("AND", "and").replace("XOR","!=").replace("OR", "or")
formula = formula.replace("F", "f").replace("T", "t")
for name in list(values.keys()):
    if name != "F" and name != "T":
        formula = formula.replace(name, values[name])

if "F" in values.keys():
    formula = formula.replace("f", values["F"])
if "T" in values.keys():    
    formula = formula.replace("t", values["T"])

output = False
for variable in ["True", "False"]:
    guess = formula.replace("X", variable)
    if eval(guess) == True:
        output = True
        break

if output == True:
    print("Satisfiable")
else:
    print("Unsatisfiable")
