#include <bits/stdc++.h>

using namespace std;

int main(){
    int n; cin >> n; cin.ignore();
    int sum = 0;
    for(int i=0; i<n; i++){
        int x; cin >> x; cin.ignore();
        bool is_prime = x > 1;
        for(int d=2; d<x; d++) 
            if(x % d == 0) 
                is_prime = false;
        if(is_prime) 
            sum += x;
    }
    cout << sum << endl;
}
