import java.util.*;

class Solution {
    public static void main(String args[]) {
        //I love playing Clap@7 ¯\_(ツ)_/¯  
       Scanner in = new Scanner(System.in);
       int N = in.nextInt();
       int counter = 0;
       for(int i = 1; i <= N; i++){
       if(i%7 == 0 || sum(i))
       {
            counter++;   
       }
       }
       System.out.println(counter);
    }
    public static boolean sum(int n)
    {
        int s = 0;
        while(n != 0)
        {
            int d = n % 10;
            if(d == 7)
            return true;

            s += d;
            n = n/10;
        }
        if(s % 7 == 0)
        return true;
        else
        return false;
    }
}
