#include <iostream>
using namespace std;
int main()
{
    int var=0;
    string s;
    while(s!="exit")
    {
        cin>>s;
        if(s=="inc") var++;
        if(s=="dec") var--;
        if(s=="double") var*=2;
        if(s=="half") var/=2;
        if(s=="print") cout<<var;
        if(s=="exit") return 0;
    }
}
