from collections import defaultdict
from itertools import combinations
import sys

def can_color_graph(n, edges, k):
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    colors = [-1] * n

    def is_safe(node, c):
        for neighbor in graph[node]:
            if colors[neighbor] == c:
                return False
        return True

    def graph_coloring(node):
        if node == n:
            return True
        for c in range(k):
            if is_safe(node, c):
                colors[node] = c
                if graph_coloring(node + 1):
                    return True
                colors[node] = -1
        return False

    return graph_coloring(0)

def max_clique_size(n, edges):
    graph = defaultdict(set)
    for u, v in edges:
        graph[u].add(v)
        graph[v].add(u)

    max_size = 0

    for r in range(1, n + 1):
        for vertices in combinations(range(n), r):
            if all(v in graph[u] for u, v in combinations(vertices, 2)):
                max_size = max(max_size, r)

    return max_size

def main():
    input = sys.stdin.read
    data = input().splitlines()

    n, m = map(int, data[0].split())
    edges = [tuple(map(int, line.split())) for line in data[1:m + 1]]
    k = int(data[m + 1])

    if can_color_graph(n, edges, k):
        max_clique = max_clique_size(n, edges)
        print(f"YES {max_clique}")
    else:
        print("NO")

if __name__ == "__main__":
    main()
