#include <iostream>
#include <string>
using namespace std;
int main()
{
    string ip;
    cin>>ip;
    int read[4]={0,0,0,0};
    int item=0;
    for(int i=0; i<ip.length(); i++)
    {
        if(ip[i]=='0') read[item]+=0;
        if(ip[i]=='1') read[item]+=1;
        if(ip[i]=='2') read[item]+=2;
        if(ip[i]=='3') read[item]+=3;
        if(ip[i]=='4') read[item]+=4;
        if(ip[i]=='5') read[item]+=5;
        if(ip[i]=='6') read[item]+=6;
        if(ip[i]=='7') read[item]+=7;
        if(ip[i]=='8') read[item]+=8;
        if(ip[i]=='9') read[item]+=9;
        if(ip[i]=='.') item++;
    }
    int firstDigit;
    if (ip[0]=='0') firstDigit=0;
    if (ip[0]=='1') firstDigit=1;
    if (ip[0]=='2') firstDigit=2;
    if (ip[0]=='3') firstDigit=3;
    if (ip[0]=='4') firstDigit=4;
    if (ip[0]=='5') firstDigit=5;
    if (ip[0]=='6') firstDigit=6;
    if (ip[0]=='7') firstDigit=7;
    if (ip[0]=='8') firstDigit=8;
    if (ip[0]=='9') firstDigit=9;
    cout<<(read[0]+read[1]+read[2]+read[3])*firstDigit;
    return 0;
}
