const weaponDamage = {
    assault_rifle: 35,
    SMG: 25,
    sniper: 100,
    grenade_launcher: 80,
    pump_shotgun: 110,
    pistol: 20,
    RPG: 150,
    minigun: 15,
    tactical_shotgun: 75
};

let results = 0;

const N = parseInt(readline());
for (let i = 0; i < N; i++) {
    const ROW = readline().split(" ");
    const weapon = ROW[0]
    const hp = parseInt(ROW[1])

    if (!(hp <= 0))
    {
        const damage = weaponDamage[weapon]
        const ammoNeeded = Math.ceil(hp / damage)
        results += ammoNeeded
    }
}
console.log(results)
