n=int(input())
friends=int(input())

def factorial(n):
    f = 1

    for i in range(2,n+1):
        f *= i
    return f


def combinations(n,k):
    return factorial(n)/(factorial(k)*factorial(n-k))

best_cmb = 0

winners = []

for i in range(friends):
    friend,k=input().split()
    k=int(k)
    cmb = combinations(n,k)
    if cmb > best_cmb:
        winners = [friend]
        best_cmb = cmb
    elif cmb == best_cmb:
        winners.append(friend)

print(" ".join(winners))
