#include <iostream>
#include <string>
#include <vector>
#include <algorithm>    //  std::min_element, std::max_element
#include <tuple>        //  std::tuple, std::get, std::make_tuple, std::tie
#include <iomanip>      //  std::fixed, std::setprecision

struct comp {
    bool operator()(auto const& t1, auto const& t2) {
        return std::get<1>(t1) < std::get<1>(t2);
    }
};

int main()
{
    int N;
    std::cin >> N;
    std::vector<std::tuple<std::string, float>> objects;
    for (int i = 0; i < N; i++) {
        std::string o; float v;
        std::cin >> o >> v;
        objects.push_back(std::make_tuple(o, v));
    }

    std::cout << std::fixed << std::setprecision(2);

    std::string o1; float spd1;
    std::tie(o1, spd1) = *std::min_element(objects.begin(), objects.end(), comp());
    float time = 2*spd1/9.8;                  //  t = -u²/2a
    std::cout << o1 << " " << time << '\n';

    std::string o2; float spd2;
    std::tie(o2, spd2) = *std::max_element(objects.begin(), objects.end(), comp());
    float height = spd2*spd2 / (4*9.8/2);   //  parabola, vertex_y = c² - b² / 4a

    std::cout << o2 << " " << height << '\n';

}
