#include <iostream>
#include <string>
#include <vector>

void close(std::vector<std::string>& animal, char eye)
{
    for (auto& line : animal)
    {
        for (auto& c : line)
        {
            if (c == eye)
            {
                c = '-';
            }
        }
    }
}

void blink(std::vector<std::string>& animal, char eye)
{
    for (auto& line : animal)
    {
        for (auto& c : line)
        {
            if (c == eye)
            {
                c = '-';
                break;
            }
        }
    }
}

void flip(std::vector<std::string>& animal)
{
    for (auto& line : animal)
    {
        std::reverse(line.begin(), line.end());
        for (auto& c : line)
        {
            if (c == '/')
            {
                c = '\\';
            }
            else if (c == '\\')
            {
                c = '/';
            }
            else if (c == '(')
            {
                c = ')';
            }
            else if (c == ')')
            {
                c = '(';
            }
            else if (c == '<')
            {
                c = '>';
            }
            else if (c == '>')
            {
                c = '<';
            }
            else if (c =='{')
            {
                c = '}';
            }
            else if (c == '}')
            {
                c = '{';
            }
        }
    }
}

int main()
{
    std::string eye;
    getline(std::cin, eye);
    int h;
    std::cin >> h; std::cin.ignore();
    int w;
    std::cin >> w; std::cin.ignore();
    int n;
    std::cin >> n; std::cin.ignore();
    std::vector<std::string> modifiers;
    for (int i = 0; i < n; i++)
    {
        std::string modifier;
        getline(std::cin, modifier);
        modifiers.push_back(modifier);
    }

    std::vector<std::string> animal;
    for (int i = 0; i < h; i++)
    {
        std::string line;
        getline(std::cin, line);
        animal.push_back(line);
    }

    for (const auto& modifier : modifiers)
    {
        if (modifier == "close")
        {
            close(animal, eye[0]);
        }
        else if (modifier == "blink")
        {
            blink(animal, eye[0]);
        }
        else if (modifier == "flip")
        {
            flip(animal);
        }
    }

    for (const auto& line : animal)
    {
        std::cout << line << std::endl;
    }
}
