#include <iostream>
#include <string>
using namespace std;


int main()
{
    string w;
    long unsigned int ascChar, sum=0;

    getline(cin, w);

    for (int i=0;i<w.length();i++)
    {
        ascChar = w[i];
        sum+=ascChar;
    }
    cout<<sum%256<<"."<<(2*sum)%256<<"."<<(3*sum)%256<<"."<<(4*sum)%256;
    return 0;
}
