const numberDoors = parseInt(readline());
const chooseDoor = parseInt(readline());
const loseDoor = parseInt(readline());

console.log(
    new Array(numberDoors).fill(0)
        .map((x,i)=>i+1)
        .filter(x=>x!=chooseDoor && x!=loseDoor)
        .join(" ")
)

f=x=>(Math.floor(x*100)/100).toFixed(2)
console.log(
    new Array(numberDoors).fill(0).map((x,i)=>i+1).map(x=>{
        p= 100/numberDoors;
        if(x==chooseDoor) return f(p);
        if(x==loseDoor) return '0.00';
        return f((100-p) / (numberDoors - 2))
    }).join(" ")
)
