def findStrongestCharacter(characters):
    max_power = -1
    strongest_chars = []

    for character in characters:
        name, power = character.split()
        power = int(power)

        if power > max_power:
            max_power = power
            strongest_chars = [name]
        elif power == max_power:
            strongest_chars.append(name)

    return sorted(strongest_chars) if strongest_chars else None

# Example Usage
N = int(input())
characters = [input().strip() for _ in range(N)]
result = findStrongestCharacter(characters)
if result is not None:
    print("\n".join(result))
else:
    print("None")
