using System;

char operation = Console.ReadLine()[0];
int a = Convert.ToInt32(Console.ReadLine(), 2);
int b = Convert.ToInt32(Console.ReadLine(), 2);
int c = Convert.ToInt32(Console.ReadLine(), 2);
int result = 0;

switch(operation){
    case 'a': result = a & b; break;
    case 'x': result = a ^ b; break;
    case 'L': result = (a << b) | (a >> (8 - b)); break;
    case 'R': result = (a >> b) | (a << (8 - b)); break;
    default:throw new Exception("Unknown operator.");
}
//truncate to 8 bit because of the shifts operators on 32 bit
result = (byte)result;
Console.WriteLine((c==result ? "true " : "false ") + Convert.ToString(result, 2).PadLeft(8, '0'));
