scrub = lambda text: set(each.lower() for each in text if each.isalpha())
excerpts = [scrub(input()) for _ in range(13)]

alphabet = {}
alphabet['English'] = letters = set('abcdefghijklmnopqrstuvwxyz')

alphabet['Danish'] = letters - set('qz') | set('æåø')
alphabet['Estonian'] = letters - set('cfqwxy') | set('šžõäöü')
alphabet['Finnish'] = letters - set('bfqwx') | set('äö')
alphabet['French'] = letters | set('çœëïüàèùâêîôûé')
alphabet['German'] = letters | set('ßäöü')
alphabet['Irish'] = letters - set('jkqvwxyz') | set('áéíóú')
alphabet['Italian'] = letters - set('jkwxy') | set('àèìòùé')
alphabet['Portuguese'] = letters - set('kw') | set('çãõàâêôáéíóú')
alphabet['Spanish'] = letters - set('kw') | set('ñüáéíóú')
alphabet['Swedish'] = letters - set('qw') | set('åäö')
alphabet['Turkish'] = letters - set('qwx') | set('ğçşıöü') | {'İ'.lower()}
alphabet['Welsh'] = letters - set('jkqvxz') | set('ŵŷâêîôû')

matches = lambda text: set(tongue for tongue in alphabet if text <= alphabet[tongue])
compatible = [matches(excerpt) for excerpt in excerpts]
# Create the converse of compatible, going from language to excerpt index.
choices = {tongue: set() for tongue in alphabet}
for index, languages in enumerate(compatible):
    for tongue in languages:
        choices[tongue].add(index)

def eliminate(excerpt, language):
    global compatible, choices
    # Maintain invariants with the excerpt in this language excluded.
    removals = {(excerpt, tongue) for tongue in compatible[excerpt]}
    removals |= {(index, language) for index in choices[language]}
    for index, tongue in removals:
        compatible[index].remove(tongue)
        choices[tongue].remove(index)

def reduction():
    global compatible, choices
    # Find an excerpt for which only a single matching language remains.
    for index in range(13):
        if len(compatible[index]) == 1:
            language = list(compatible[index])[0]
            return index, language
    # Find a langugage associated with only a single remaining excerpt.
    for language in choices:
        if len(choices[language]) == 1:
            index = list(choices[language])[0]
            return index, language
    raise

result = [None] * 13
for _ in range(13):
    index, language = reduction()
    eliminate(index, language)
    result[index] = language
for language in result:
    print(language)
