import java.util.*;

/**
 * Train: Heavy Passenger Traffic Rearrangement solution
 **/
class Solution {
    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        String[] sections = in.nextLine().split(" - ");

        for (int s = 0; s < sections.length; s++) {
            int[] passengers = Arrays.stream(sections[s].split(" "))
                                     .mapToInt(Integer::parseInt)
                                     .toArray();
            Arrays.sort(passengers);
            System.out.print(String.join(" ", Arrays.stream(passengers)
                                                    .mapToObj(String::valueOf)
                                                    .toArray(String[]::new)));
            if(s < sections.length -1) {
                System.out.print(" - ");
            }
        }
        in.close();
    }
}
