#include <iostream>
std::string escapeDate(int level) {
    return level <= 3 ? "I'll need to wake up early tomorrow." :
               level <= 6 ? "I'm feeling unwell, I need to leave." :
                          level <= 9 ? "I forgot I have an important meeting soon." :
                                     "Sorry, but I think this isn't working out.";
                                     }
                                     int main() {
                                         int level; 
                                             std::cin >> level;
                                                 std::cout << escapeDate(level) << std::endl;
                                                 }

