import java.util.Scanner;

public class Solution {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String input = scanner.nextLine(); 

        int left = 0;
        int right = input.length() - 1;
        boolean isPalindrome = true;
        boolean isAlmostPalindrome = false;

        while (left < right) {
            if (input.charAt(left) != input.charAt(right)) {
                isPalindrome = false;
                boolean skipLeft = true;
                boolean skipRight = true;

                for (int i = left + 1, j = right; i < j; i++, j--) {
                    if (input.charAt(i) != input.charAt(j)) {
                        skipLeft = false;
                        break;
                    }
                }

                for (int i = left, j = right - 1; i < j; i++, j--) {
                    if (input.charAt(i) != input.charAt(j)) {
                        skipRight = false;
                        break;
                    }
                }

                if (skipLeft || skipRight) {
                    isAlmostPalindrome = true;
                }
                break;
            }
            left++;
            right--;
        }

        if (isPalindrome) {
            System.out.println("Already a palindrome");
        } else if (isAlmostPalindrome) {
            System.out.println("Almost a palindrome");
        } else {
            System.out.println("Not a palindrome");
        }

        scanner.close();
    }
}
