name=input()
have_upper_alpa=False
have_lower_alpa=False
have_num=False
last_is_upper=False
if name[-1].isupper():
    last_is_upper=True

for i in name:
    if i.isupper():
        have_upper_alpa=True
    elif i.islower():
        have_lower_alpa=True
    elif i.isnumeric():
        have_num=True

if have_upper_alpa and have_lower_alpa and have_num:
    print("Alien Name")
elif last_is_upper:
    print("Star Name")
else:
    print("Human Name")
