/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/

const N = parseInt(readline());
let votes = { "D": 0, "R": 0 }; // Initialize vote tally for Democrats and Republicans

for (let i = 0; i < N; i++) {
    var inputs = readline().split(' ');
    const electors = parseInt(inputs[1]);
    const winner = inputs[2];

    // Add the electoral votes to the winning party's total
    votes[winner] += electors;
}

// Determine the winner based on total electoral votes
if (votes["D"] > votes["R"]) {
    console.log("Democrats");
} else if (votes["R"] > votes["D"]) {
    console.log("Republicans");
} else {
    console.log("Draw");
}
