const n = parseInt(readline());

// Write an answer using console.log()
// To debug: console.error('Debug messages...');

const result = [];

for (let i = 1; i <= n; i++) {
    if ((i % 3) + (i % 5) == 0) result.push("MeowPurr");
    else if (i % 5 == 0) result.push("Purr");
    else if (i % 3 == 0) result.push("Meow");
    else result.push(i.toString());
}

if (n === 0) {
    console.log("*voosh*");
} else {
    console.log(result.join(" "));
}
