from sys import stderr
from typing import List


noteTypes = input()
n = int(input())


money: List[int] = [int(x) for x in noteTypes.split()]
money = list(reversed(money))

if min(money) > n:
    print('Impossible')
else:
    each_kind: List[int] = []
    while n > 0:
        for type in money:
            each_kind.append(n // type)
            n = n % type

    output: List[str] = []
    for index, type in enumerate(each_kind):
        if type != 0:
            output.append(f'{type}x{money[index]}')
    print(*output)
