# Read the input into a padded 3D array.
L, W, H = [int(i) + 2 for i in input().split()]
pad = lambda: ['o' for _ in range(W)]
row = lambda: [list('o' + each + 'o') for each in input().split()]
data = [[pad() for _ in range(H)]]
data += [[pad()] + row() + [pad()] for _ in range(L - 2)]
data += [[pad() for _ in range(H)]]
# Flood fill the exterior with x's.
todo = [[0, 0, 0]]
while todo:
    x, y, z = todo.pop()
    if data[x][y][z] != 'o':
        continue
    data[x][y][z] = 'x'
    for dim in range(3):
        for sign in (-1, 1):
            coord = [x, y, z]
            coord[dim] += sign
            a, b, c = coord
            if 0 <= a < L and 0 <= b < H and 0 <= c < W:
                todo.append(coord)
# Make sure the test case is correctly designed.
for x in range(L):
    for y in range(H):
        for z in range(W):
            if data[x][y][z] != 'x':
                continue
            for a in (x - 1, x, x + 1):
                if 0 > a or a >= L:
                    continue
                for b in (y - 1, y, y + 1):
                    if 0 > b or b >= H:
                        continue
                    for c in (z - 1, z, z + 1):
                        if 0 > c or c >= W:
                            continue
                        if data[a][b][c] == 'o':
                            print('Exterior', x, y, z)
                            print('Interior', a, b, c)
                            raise Exception('Potential leak found!')
# Count the remaining symbols.
counts = {'o': 0, '#': 0}
for a in range(L):
    for b in range(H):
        for c in range(W):
            symbol = data[a][b][c]
            if symbol != 'x':
                counts[symbol] += 1
# Compute the surface area.
surface = 0
for dim in range(3):
    for x in [0] if dim == 0 else range(L):
        for y in [0] if dim == 1 else range(H):
            for z in [0] if dim == 2 else range(W):
                coord = [x, y, z]
                for _ in range([L, H, W][dim] - 1):
                    a, b, c = coord
                    symbol = data[a][b][c]
                    coord[dim] += 1
                    a, b, c = coord
                    if {symbol, data[a][b][c]} == {'x', '#'}:
                        surface += 1
material = counts['#']
density = material/(material + counts['o'])
print(f"{round(surface/material, 4):.4f} {round(density, 4):.4f}")
