lip=[]
n = int(input())
for i in range(n):
    trigger=0
    ip = input()
    if ip.count('.')==3:
        for o in ip.split('.'):
            if not o.isdigit or int(o) not in range(0,256):
                trigger=1
                break
        if not trigger:
            lip.append(ip)

print(n-len(lip))
if n-len(lip)==n:
    print("INVALID ADDRESS TABLE")
elif n-len(lip)==0:
    print("MASTER COMRADE, NICE JOB!!!!!!")
else:
    for i in lip:
        print(i)
