def skibidi_challenge(n, messages):
    results = []
    for message in messages:
        index_ohio = message.find('ohio')
        index_gyatt = message.find('gyatt')
        if index_ohio != -1 and index_gyatt != -1:
            if index_ohio < index_gyatt:
                results.append("YES")
            else:
                results.append("NO")
        else:
            results.append("MISSING")
    return results


n = int(input())
messages = [input() for _ in range(n)]

output = skibidi_challenge(n, messages)

for line in output:
    print(line)
