n = int(input())
available_parts = input()
m = int(input())
broken_part = input()

found_match = "Missing"

for i in range(n - m + 1):
    mismatch_count = 0
    for j in range(m):
        if available_parts[i + j] != broken_part[j]:
            mismatch_count += 1
            if mismatch_count > 1:
                break
    if mismatch_count == 1:
        found_match = "Alternative"
        break 
    elif mismatch_count == 0:
        found_match = "Original"
        break
print(found_match)
