import java.util.*;
import java.io.*;
import java.math.*;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        
        String line = in.nextLine();
        
        System.out.println(push(line));
    }
    
    static String push(String str) {
        int initV = -1; 
        char cntV = 'a';
        String vowels = "AEIOU";
        for (int i = 0; i < str.length(); i++) {
            char cnt = str.charAt(i);
            if (vowels.indexOf(cnt) == -1) {continue;}
            if (initV == -1) {initV = i;}
            else {str = str.substring(0,i)+cntV+str.substring(i+1);}
            cntV = cnt;
        }
        if (initV != -1) {str = str.substring(0,initV)+cntV+str.substring(initV+1);}
        return str;
    }
}
