import collections
import random
import sys

def revised_knowledge(hypothetical, cleared, unchecked):
    new_clears = set(cleared)
    new_checks = []
    for clues in unchecked:
        if hypothetical in clues:
            # if hypothetical is true then all others aren't
            new_clears |= clues  # except hypothetical, removed below
        else:
            new_checks.append(clues)
    new_clears.remove(hypothetical)
    return new_clears, new_checks

def check_hypothesis(found, cleared, unchecked):
    if not unchecked:
        return True  # all done when nothing more to satisfy
    for clues in unchecked:
        if clues <= cleared:
            return False  # there's evidence that can't be satisfied
    for hypothetical in random.choice(unchecked):
        if hypothetical in cleared:
            continue
        # attempt to satisfy constraints by setting hypothetical to true
        found.add(hypothetical)
        new = revised_knowledge(hypothetical, cleared, unchecked)
        if check_hypothesis(found, *new):
            return True
        found.remove(hypothetical)
    return False

def satisfiable(evidence):
    for suspect in evidence[0]:
        found = {suspect}
        knowledge = revised_knowledge(suspect, frozenset(), evidence)
        if check_hypothesis(found, *knowledge):
            print(', '.join(sorted(found)), file=sys.stderr, flush=True)
            yield suspect

def read_line():
    line = input()
    clues = [each.strip() for each in line.split(',')]
    assert line == ', '.join(clues)
    result = set(clues)
    assert len(clues) == len(result) > 0
    return result

def read_input():
    L, N = [int(each) for each in input().split()]
    result = [read_line() for _ in range(L)]
    assert len(result[0]) == N
    counts = collections.Counter()
    for clues in result:
        counts += collections.Counter(clues)
    assert min(counts.values()) > 1 or max(counts.values()) == 1
    return result

print('\n'.join(satisfiable(read_input())))

