data.solution:
beam = input()
pos = beam.find("^")
anti_clockwise = 0
clockwise = 0

n = int(input())
for i in range(n):
    box_identifier, mass = input().split()
    weight = float(mass) * 9.81
    if beam.find(box_identifier) < pos:
        anti_clockwise += weight * (pos-beam.find(box_identifier))
    else:
        clockwise +=  weight * (beam.find(box_identifier)-pos)

net_moments = clockwise - anti_clockwise
if net_moments == 0:
    print("BALANCED")
elif net_moments < 0:
    print("ANTI-CLOCKWISE")
else:
    print("CLOCKWISE")

print(f"{abs(round(net_moments))}Nm")
