# Read inputs from Standard Input.
# Write outputs to Standard Output.
# Please, do not use fileinput module to read Standard Input.

import sys
from itertools import product

def teste(grille):
    for l in grille:
        if len(set(l))!=4:
            return False
    for i in range(4):
        if len({grille[j][i] for j in range(4)})!=4:
            return False
    for i in range(2):
        for j in range(2):
            if len(set(grille[2*i][2*j:2*j+2]+grille[2*i+1][2*j:2*j+2]))!=4:
                return False
    return True

grille=[[int(i) for i in raw_input()] for _ in range(4)]
print>> sys.stderr,grille
nz=sum(l.count(0) for l in grille)
posz=[[i,j] for i in range(4) for j in range(4) if grille[i][j]==0]
grillebis=list(grille)

for essai in product([1,2,3,4],repeat=nz): # Not optimized but does the job slowly
    for i in range(nz):
        p=posz[i]
        grillebis[p[0]][p[1]]=essai[i]
    if teste(grillebis):
        break

for l in grillebis:
    print("".join([str(i) for i in l]))
