const fridgeRack = readline();
const burger=['︶','~~~','<>','—','---','︵'];

function getSortIndices(arr) {
    const indexedArray = arr.map((value, index) => ({ value, index }));
    indexedArray.sort((a, b) => burger.indexOf(a.value) - burger.indexOf(b.value));
    return indexedArray.map(item => item.index);
}

let fridgeRackArr=fridgeRack.split(" ");

let sortedIndices=getSortIndices(fridgeRackArr);

if(fridgeRackArr.indexOf('—')==-1 && sortedIndices.length<5){
    console.log("missing ingredients");
}else if(fridgeRackArr.indexOf('—')!=-1 && sortedIndices.length<6){
    console.log("missing ingredients");
}else{
    console.log(sortedIndices.join(' '));
}
